const { POST_REGISTRATION_SUCCESS, POST_REGISTRATION_REQUEST, POST_REGISTRATION_FAILURE } = require("../Action/registration.action")

const initialState ={};

export function registrationReducer(state = initialState,action){
    console.log(action,'action')
    switch(action.type){
        case POST_REGISTRATION_REQUEST:
        return {
            ...state,
           loading:true
        }
        case 'POST_REGISTRATION_SUCCESS':
            
        return{
            postUsers:action.payload,
            
        }
        case POST_REGISTRATION_FAILURE:
        return{
            loading:false,
            postUsers:'test',
            error:action.payload
        }
        default:
        return state;
    }
    return state;
}
