import React, { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Header from './Header'

import { postRegistration } from '../Redux/Action/registration.action';
import { get_user_list } from '../Redux/Action/user.action';
import { connect } from 'react-redux';

const PersonalDetails = (props) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [mobile_number, setMobile] = useState('');
    const [address, setAddress] = useState('');
    const [password, setPassword] = useState('');
    const [c_password, setConPassword] = useState('');
    
    const [submited, setIssubmited] = useState('');
    const [inputList, setInputList] = useState([{ firstName: ""}]);

    //const Data = { "name": name, "email": email, "mobile_number": mobile_number, "address": address, "password": password, "c_password": c_password,'aadhaar':firstName }

        
    const notify = (msg) => toast(msg);
    let history = useHistory();
    const redirect = (e) => {
        console.log('ssssssssssssssssssvvvvvvvvvvv');

        if (props.postUserData.success === "true") {
            console.log('ppppppppp')
            history.push("/Login");
        }
        console.log('fffffffffff')
    }

    const addhartest =(e,index)=>{
        //setAadhaar(index.e);
        inputList[index].firstName = e;
        setInputList(inputList)
        console.log(inputList,'inputList');
       

    }

    const fronSubmit = (e) => {
        e.preventDefault();
        props.postUser(formData);
        redirect();
    }
    const RemoveAadhaar =(index)=>{
        if(inputList.length !== 1)
        {
            const list = [...inputList];
            list.splice(index, 1);
            setInputList(list);
        }

    }

    const formData = new FormData()

    // Update the formData object

    formData.append('name',name)
    formData.append('email',email)
    formData.append('mobile_number',mobile_number)
    formData.append('address',address)
    formData.append('password',password)
    
    inputList.map((item,key)=>{
       formData.append('aadhaar'+(key+1),item)
    })



    const AddAadhaar =(e)=>{
        
        setInputList([...inputList,{ firstName: ""}])

        console.log(inputList)
        console.log(c_password,'pass')
    }

    // useEffect(() => {
    //     postRegistration(Data)
    //     console.log(Data,"testesdddd")
    //   },[]) ////// empty dependency array so fetch user() dispatch only once

    return (
        <>
            <Header />
            <ToastContainer />
            <div className="container register-form">
                <div className="form">
                    <div className="note">
                        <p>Personal Details.</p>

                    </div>
                    <form onSubmit={fronSubmit}>
                        <div className="form-content">
                            <div className="row">
                                <div className="col-md-6">
                                    <div className="form-group">
                                        <input type="text"
                                            className="form-control"
                                            placeholder="Your Name *"
                                            name="name"
                                            value={name}
                                            onChange={e => setName(e.target.value)}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <input type="email"
                                            className="form-control"
                                            placeholder="Enter your Email*"
                                            name="email"
                                            value={email}
                                            onChange={e => setEmail(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <div className="col-md-6">
                                    <div className="form-group">
                                        <input type="number"
                                            className="form-control"
                                            placeholder="Enter your Mobile No *"
                                            name="mobile_number"
                                            value={mobile_number}
                                            onChange={(e) => setMobile(e.target.value)}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <textarea type="text"
                                            className="form-control"
                                            placeholder="Enter your address *"
                                            name="address"
                                            value={address}
                                            onChange={(e) => setAddress(e.target.value)}
                                        />
                                    </div>
                                </div>
                            </div>
                            <div className="row">

                            
                                <div className="col-md-12">
                                {inputList.map((List, index) => {
                                return (
                                   
                                <div key={index}>
                                    <div className="form-group">
                                        <label>Aadhar Card</label>
                                        <input type="file" 
                                            name = "aadhaar"
                                            value={inputList.List}
                                            // onChange={(e)=>setAadhaar(e.target.files[0])}
                                            onChange ={(e)=>addhartest(e.target.files[0],index)}
                                            
                                        />
                                    </div>
                                </div>
                                )
                                })}

                                <input type="button" onClick={(e)=>AddAadhaar(e)} className="btnSubmit" value="Addmore"/>
                                <input type="button" onClick={(e)=>RemoveAadhaar(e)} className="btnSubmit" value="Remove"/>
                                </div>
                            
                               

                                <div className="col-md-6">
                                    <div className="form-group">
                                        <label>Profile Image</label>
                                        <input
                                            type="file"
                                            className="form-control"
                                            name="image"

                                            onChange={(e) => setConPassword(e.target.files[0])}
                                        />
                                    </div>
                                    
                                </div>
                            </div>
                            <button type="submit" className="btnSubmit">Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </>


    )
}
const mapStateToProps = (state) => {
    return {
        postUserData: state.postUser.postUsers,
        userData: state.users
    }
}

const mapDispatchToProps = dispatch => {
    return {
        postUser: (Data) => dispatch(postRegistration(Data)),
        getUser: () => dispatch(get_user_list())
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(PersonalDetails)
