import React,{useEffect ,useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Eheaders from './Eheaders'
import {fetchProductDetails} from '../Redux/Action/fetchProduct.action';
import { connect } from 'react-redux';



const ProductListing = (props)=>{
    useEffect(()=>{
        props.fetchProduct() ;
        console.log(props.fetchProductList.productList,'fetchProductListfetchProductList')
    },[]);

    return(
        <>
        <Eheaders/>
        {props.fetchProductList && props.fetchProductList.productList &&props.fetchProductList.productList.map((product)=>{

            <div className="ui grid container">
                <div className="ui link cards">
                    <div className="cards">
                        <div className="image">
                            <div className="content"></div>
                            <div className="header">{title}</div>
                            <div className="meta price">${price}</div>

                        </div>
                    </div>
                </div>
            </div>
         })

        }
        </>
    )

}
const mapStateToProps = (state) =>{
    return{
        fetchProductList :state.productList
    }
  }
  
  const mapDispatchToProps = dispatch =>{
    return{
       fetchProduct:()=> dispatch(fetchProductDetails())         
    }
  }
export default connect(mapStateToProps,mapDispatchToProps)(ProductListing)
