import axios from 'axios'
import { fetchUsersRequest,postUsersSuccess,fetchUsersFailure } from '../Action/user.action'
const { FETCH_USER_SUCCESS, FETCH_USER_REQUEST, FETCH_USER_FAILURE } = require("../Action/user.action")

const initialState ={
    loading:false,
    users:[],
    error:''
}

export const userReducer =(state = initialState,action) =>{
    switch(action.type){
        case FETCH_USER_REQUEST:
        return {
            ...state,
           loading:true
        }
        case postUsersSuccess:
        return{
            loading:false,
            users:action.payload,
            error:''
        }
        case FETCH_USER_FAILURE:
        return{
            loading:false,
            users:[],
            error:action.payload
        }
        default:return state
    }

}
