const { POST_REGISTRATION_SUCCESS, POST_REGISTRATION_REQUEST, POST_REGISTRATION_FAILURE } = require("../Action/registration.action")

const initialState ={
    loading:false,
    users:[],
    error:''
}

export const  registrationReducer =(state = initialState,action) =>{
    switch(action.type){
        case POST_REGISTRATION_REQUEST:
        return {
            ...state,
           loading:true
        }
        case POST_REGISTRATION_SUCCESS:
        return{
            loading:false,
            users:'success',
            error:''
        }
        case POST_REGISTRATION_FAILURE:
        return{
            loading:false,
            users:[],
            error:action.payload
        }
        default:return state
    }

}
